package assignments;

public class Forsum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
		int n=1000;
		for(int i=1;i<=n;++i)//for(int i=1; i>=n; --i) Here we can write like this for sum of natural numbers
		{
		sum=sum+i;
		}
		System.out.println("sum="+sum);
		

	}

}
